con:config.script:
String lang = "ru"
String text = "Россмя"
String expected = "Россия"

if (testRunner.testCase.getPropertyValue( "lang" ).isEmpty())
{
	testRunner.testCase.setPropertyValue( "lang", lang )
	testRunner.testCase.setPropertyValue( "text", text )
	testRunner.testCase.setPropertyValue( "expected", expected )
}

else if(testRunner.testCase.getPropertyValue( "lang" ) == "ru")
{
	lang = "en"
	text = "Englbnd"
	expected = "England"

	testRunner.testCase.setPropertyValue( "lang", lang )
	testRunner.testCase.setPropertyValue( "text", text )
	testRunner.testCase.setPropertyValue( "expected", expected )
}

else if(testRunner.testCase.getPropertyValue( "lang" ) == "en")
{
	lang = "uk"
	text = "Украина"
	expected = "Україна"

	testRunner.testCase.setPropertyValue( "lang", lang )
	testRunner.testCase.setPropertyValue( "text", text )
	testRunner.testCase.setPropertyValue( "expected", expected )
}

else if(testRunner.testCase.getPropertyValue( "lang" ) == "uk")
{
	testRunner.gotoStepByName( "tearDown")
}
